import React from 'react';
import { Carousel, Container, Row, Col, Button } from 'react-bootstrap';
import styles from '../cssPage/potfolio.module.css'; // Add styles if needed

const projectImg = [
  { page: 'cart page' },
  { img: 'image/2.png', page: 'vendor page' },
  { img: 'image/3.png', page: 'home' },
  { img: 'image/4.png', page: 'product' },
  { img: 'image/5.png', page: 'login' },

];

const ProjectImg =[
    {
        img:['image/1.png', 'image/2.png', 

        ]
    }
]

const FoodAppShowcase = () => {
  return (
    <Container fluid className={styles.showcaseContainer}>
      {/* Title Section */}
      <h2 className="text-center py-3">Food App Project</h2>

      {/* Carousel Section */}
      <Row>
        <Col>
          <Carousel className={styles.carousel} interval={3000}>
            {projectImg.map((img, index) => (
              <Carousel.Item key={index}>
                
                <img
                  className="d-block w-100"
                  src={img.img}
                  alt="Screenshot 1"
                />
              </Carousel.Item>
            ))}
          </Carousel>
        </Col>
      </Row>

      {/* Description Section */}
      <Row className={` mt-4 p-4 ${styles.pageWrapper}`}>
      <Col className="text-center">
  <h3>About the App</h3>
  <p>
    This food app is one of the key projects in my portfolio, where I gained valuable experience in various aspects of web development. <br/>
    I built the app to streamline food ordering, featuring a user-friendly interface and seamless payment integrations. <br/>
    It was developed using React for the front end, Node.js for the back end, and MongoDB for the database.
  </p>
  <p>
    Throughout the development process, I learned how to implement key features such as payment integration, <br/>
    email-based authentication and verification, and much more. <br/>
    The app allows users to browse menus, place orders, and track delivery status with ease. <br/>
    Fully responsive and optimized for performance, it ensures a smooth experience across all devices.
  </p>
  <p>
    The technologies used in this project include React, Bootstrap, Node.js, Express, and MongoDB. <br/>
    The app aims to enhance user convenience while also improving restaurant management systems. <br/>
    Below are images showcasing the app's features and user interface.
  </p>
    <Button variant='info'>Click here to view</Button>
</Col>

      </Row>
    </Container>
  );
};

export default FoodAppShowcase;
